    // Receives search input and filters length to determine if asset or address
    function searchFilter(searchValue) {

        // If text input is not length of address then search for asset
        if(searchValue.length < 33) {
            result = Fetch("asset", searchValue)
            // setResult(result)
        } else {
        // Search an address
            result = Fetch("address", searchValue)
            // setResult(result)
        }
    }
    searchFilter(props)

    return result
}

export default Search
