import React, { useEffect } from "react"

function Gallery(props) {

    // FILTER
    // On filter change do not display filtered pepes
    useEffect(() => {
        console.log((document.getElementsByClassName("original")).length > 0)
        if((document.getElementsByClassName("original").length > 0) && (props.pepeFilter.original == false)) {
            document.getElementsByClassName("original").style.visibility = "hidden";
        } else if ((document.getElementsByClassName("original").length > 0) && (props.pepeFilter.original == true)) {
            document.getElementsByClassName("original").style.visibility = "visible";
        }

        if(((document.getElementsByClassName("original")).length > 0) && (props.pepeFilter.fake == false)) {
            document.getElementsByClassName("fake").style.visibility = "hidden";
        } else if (((document.getElementsByClassName("original")).length > 0) && (props.pepeFilter.fake == true)) {
            document.getElementsByClassName("fake").style.visibility = "visible";
        }
    }, [props.pepeFilter])

    return (
        <div className="gallery">
            {props.wallet}
        </div>
    )
}

export default Gallery
