import useState from "react"

function Fetch(type, value) {
    // const [ data, setData ] = useState(null)
    let data = null

    // Receives type [asset, address] and searches api for value
    const fetchData = async (type, value) => {
        console.log(`https://xchain.io/api/${type}/${value}`)
        const res = await fetch(`https://xchain.io/api/${type}/${value}`)
        const json = await res.json()
        return json["data"]
    }
    fetchData(type, value)
        .then(res => {
            // setData(res)
            data = res
            console.log("Set data: " + data)
        })

    // Returns API JSON data
    return data
}

export default Fetch
