    function onChange(input) {setWallet(input)}

    // On wallet update begin populating gallery
    useEffect(() => {
        console.log("Wallet changed: ")
        console.log(wallet)
    }, [wallet])

    return (
        <NavBar 
            wallet={wallet} 
            onChange={onChange}
        />
    );
}

export default App;
