import './App.css'
import React, { useEffect, useState } from "react"
import NavBar from "./components/NavBar"
import Gallery from "./components/Gallery"
import FindSrc from "./components/FindSrc"
import CreateCard from './components/CreateCard'

// TODOD:
// a. Load only when scroll down
// 1. Make function that shows list of all fake pepes
// 2. Make search data pull from form submit
// 3. Enter submits search not reloads page

function App() {
    // Global variables
    const [ wallet, setWallet ] = useState([])
    const [ data, setData ] = useState({})
    const [ pepeFilter, setPepeFilter ] = useState({
        original: false,
        fake: true
    })

    // Allows writing of parent data from child
    function onDataChange(input) {setData(input)}


    // On data update begin populating gallery
    useEffect(() => {
        // Filter JSON data for single asset or wallet balance
        if(data["asset"]) {

            // Get asset details
            const classes = "image-card"    // Does not require series filter
            const assetName = data["asset"]
            const assetQty = data["supply"]
            let assetSrc = FindSrc(assetName)

            // Create asset card
            setWallet(<CreateCard key={1} classes={classes} asset={assetName} quantity={assetQty} src={assetSrc} type={"asset"} />)

        } else if (data["data"]) {

            // Populate wallet with assets
            if (data["data"].length > 0) {

                let cardList = []
                let count = 0
                const assetList = data["data"]

                assetList.forEach((asset) => {
                    const series = asset["series"]
                    const classes = `image-card ${series}`
                    const assetName = asset["asset"]
                    const assetQty = asset["quantity"]
                    let assetSrc = FindSrc(assetName)
                    // If asset src is found append to list
                    if(assetSrc != "null") {
                            cardList.push(
                                <CreateCard key={count} classes={classes} asset={assetName} quantity={assetQty} src={assetSrc} type={"address"} />
                            )
                            // Continue index count
                            count += 1
                        }
                })

                // Update wallet
                setWallet(cardList)
            }

        }
    }, [data])

    // FILTER FUNCS
    function onFilterChange(type) {
        // If input is original filter toggles allow OG pepes
        if(type === "original") {
            setPepeFilter(prevState => ({
                original: !prevState.original,
                fake: prevState.fake
            }))
        } else if (type === "fake") {
        // If input is fake filter toggles allow OG pepes
            setPepeFilter(prevState => ({
                original: prevState.original,
                fake: !prevState.fake
            }))
        }
        console.log(pepeFilter)
    }

    return (
        <div>
            <NavBar 
                data={data} 
                onDataChange={onDataChange}
                pepeFilter={pepeFilter}
                onFilterChange={onFilterChange}
            />
            <Gallery 
                wallet={wallet}
                pepeFilter={pepeFilter}
            />
        </div>
    );
}

export default App;
