import React, { useEffect } from "react"

function Gallery(props) {

    // FILTER
    // On filter change do not display filtered pepes
    useEffect(() => {
        console.log("Filtering")
        if(
            (props.pepeFilter.original == false) &&
            (document.getElementsByClassName("original"))
        ) {
            document.getElementsByClassName("original").style.visibility = "hidden";
        } else if (props.pepeFilter.original == true) {
            document.getElementsByClassName("original").style.visibility = "visible";
        }

        if(props.pepeFilter.fake == false) {
            document.getElementsByClassName("original").style.visibility = "hidden";
        } else if (props.pepeFilter.fake == true) {
            document.getElementsByClassName("original").style.visibility = "visible";
        }
    }, [props.pepeFilter])

    return (
        <div className="gallery">
            {props.wallet}
        </div>
    )
}

export default Gallery
