import React, { useState } from "react"
import Fetch from "./Fetch"

function Search(props) {
    const [ result, setResult ] = useState(null)

    // Receives search input and filters length to determine if asset or address
    function changeSearch(searchValue) {

        // If text input is not length of address then search for asset
        if(searchValue.length < 33) {
            result = Fetch("asset", searchValue)
            setResult(result)
        } else {
        // Search an address
            result = Fetch("address", searchValue)
            setResult(result)
        }
    }

    return result
}

export default Search
