import useState from "react"
import Fetch from "./Fetch"

function Search(props) {
    console.log("Search props: " + props)
    const [ result, setResult ] = useState(null)

    // Receives search input and filters length to determine if asset or address
    function searchFilter(searchValue) {

        // If text input is not length of address then search for asset
        if(searchValue.length < 33) {
            setResult(Fetch("asset", searchValue))
        } else {
        // Search an address
        setResult(Fetch("address", searchValue))
        }
    }
    searchFilter(props)

    return result
}

export default Search
