import React from "react"

function CreateCard(props) {
    // Takes in image src, asset name and 
    // quantity to return an image card for gallery view

    // TODO: Create alternative display for MP4

    let series = props.series
    let original = props.pepeFilter.original
    let fake = props.pepeFilter.fake

    const className = `image-card ${series}`

    const card = (
        <span 
        className="image-card"
        >
            <img 
                src={props.src}
            />
            <p className="asset-name">
                <span>{props.asset}</span>
                <span>{props.quantity}</span>
            </p>
        </span>
    )

    return (
        // <span 
        // className="image_card"
        // >
        //     {(
        //         (original) && (series == "original") ||
        //         (fake) && (series == "fake")
        //     ) ? card : null}
        // </span>
        <span 
            className={className}
        >
            <img 
                src={props.src}
            />
            <p className="asset-name">
                <span>{props.asset}</span>
                <span>{props.quantity}</span>
            </p>
        </span>
    )
}

export default CreateCard
