import React from "react"

function CreateCard(props) {
    // Takes in image src, asset name and 
    // quantity to return an image card for gallery view

    // TODO: Create alternative display for MP4

    let series = props.series
    let original = props.filter.original
    let fake = props.filter.fake
    function filter(e) {
        if((series == "original") && (original)) {
            e.target.classList.add = "visuallyhidden"
        } else if ((series == "original") && (!original)) {
            e.target.classList.remove = "visuallyhidden"
        }

        if((series == "fake") && (fake)) {
            e.target.classList.add = "visuallyhidden"
        } else if ((series == "fake") && (!fake)) {
            e.target.classList.remove = "visuallyhidden"
        }
    }

    return (
        <span 
            className={(e) => {
                filter
            }}
            style={style}
        >
            <img 
                src={props.src}
            />
            <p className="asset-name">
                <span>{props.asset}</span>
                <span>{props.quantity}</span>
            </p>
        </span>
    )
}

export default CreateCard
